mapper.update:
update dish
name = #{name}, price = #{price}, image = #{image}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser}, category_id = #{categoryId}, status = #{status},
where id = #{id}

mapper.select:
select d.*,c.name categoryName from dish d left outer join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc
